import React, { useState } from 'react';
import '../styles/AppManagementModal.css';
import AppCredentialsModal from './AppCredentialsModal';

function AppManagementModal({ app, userType, onClose, onUpdate }) {
  const [activeTab, setActiveTab] = useState('roles');
  const [newRole, setNewRole] = useState('');
  const [selectedUser, setSelectedUser] = useState('');
  const [selectedRole, setSelectedRole] = useState('');
  const [showCredentialsModal, setShowCredentialsModal] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  const handleAddRole = () => {
    if (newRole.trim()) {
      // Add role logic here
      onUpdate('role', { action: 'add', role: newRole });
      setNewRole('');
    }
  };

  const handleRemoveRole = (role) => {
    onUpdate('role', { action: 'remove', role });
  };

  const handleAssignUser = () => {
    if (selectedUser && selectedRole) {
      onUpdate('user', { 
        action: 'assign', 
        user: selectedUser, 
        role: selectedRole 
      });
      setSelectedUser('');
      setSelectedRole('');
    }
  };

  const handleManageCredentials = () => {
    setShowCredentialsModal(true);
  };

  const handleCredentialsSave = () => {
    setShowCredentialsModal(false);
    setSuccess('Credentials updated successfully');
    setTimeout(() => setSuccess(null), 3000);
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h3>Manage {app.app_name}</h3>
          <button className="close-button" onClick={onClose}>×</button>
        </div>

        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">{success}</div>}

        <div className="modal-tabs">
          <button 
            className={`tab-button ${activeTab === 'roles' ? 'active' : ''}`}
            onClick={() => setActiveTab('roles')}
          >
            Roles
          </button>
          <button 
            className={`tab-button ${activeTab === 'users' ? 'active' : ''}`}
            onClick={() => setActiveTab('users')}
          >
            Users
          </button>
          {userType === 'admin' && (
            <button 
              className={`tab-button ${activeTab === 'settings' ? 'active' : ''}`}
              onClick={() => setActiveTab('settings')}
            >
              Settings
            </button>
          )}
        </div>

        <div className="modal-body">
          {activeTab === 'roles' && (
            <div className="roles-section">
              <div className="add-role-form">
                <input
                  type="text"
                  value={newRole}
                  onChange={(e) => setNewRole(e.target.value)}
                  placeholder="Enter new role name"
                />
                <button onClick={handleAddRole}>Add Role</button>
              </div>
              <div className="roles-list">
                {app.roles?.map((role, index) => (
                  <div key={index} className="role-item">
                    <span>{role}</span>
                    <button 
                      className="remove-role"
                      onClick={() => handleRemoveRole(role)}
                    >
                      ×
                    </button>
                  </div>
                ))}
              </div>
            </div>
          )}

          {activeTab === 'users' && (
            <div className="users-section">
              <div className="assign-user-form">
                <select
                  value={selectedUser}
                  onChange={(e) => setSelectedUser(e.target.value)}
                >
                  <option value="">Select User</option>
                  {/* Add user options here */}
                </select>
                <select
                  value={selectedRole}
                  onChange={(e) => setSelectedRole(e.target.value)}
                >
                  <option value="">Select Role</option>
                  {app.roles?.map((role, index) => (
                    <option key={index} value={role}>{role}</option>
                  ))}
                </select>
                <button onClick={handleAssignUser}>Assign User</button>
              </div>
              <div className="users-list">
                {/* Add users list here */}
              </div>
            </div>
          )}

          {activeTab === 'settings' && userType === 'admin' && (
            <div className="settings-section">
              <div className="setting-item">
                <label>Application Name</label>
                <input type="text" defaultValue={app.app_name} />
              </div>
              <div className="setting-item">
                <label>Application URL</label>
                <input type="text" defaultValue={app.app_url} />
              </div>
              <button className="save-settings">Save Changes</button>
            </div>
          )}

          <div className="app-details">
            <div className="detail-group">
              <label>Application Name</label>
              <p>{app.app_name}</p>
            </div>
            <div className="detail-group">
              <label>URL</label>
              <p>{app.app_url}</p>
            </div>
            <div className="detail-group">
              <label>Description</label>
              <p>{app.description || 'No description available'}</p>
            </div>
          </div>

          <div className="modal-actions">
            <button
              className="credentials-button"
              onClick={handleManageCredentials}
              disabled={loading}
            >
              Manage Credentials
            </button>
            <button
              className="close-modal-button"
              onClick={onClose}
              disabled={loading}
            >
              Close
            </button>
          </div>
        </div>

        {showCredentialsModal && (
          <AppCredentialsModal
            app={app}
            onClose={() => setShowCredentialsModal(false)}
            onSave={handleCredentialsSave}
          />
        )}
      </div>
    </div>
  );
}

export default AppManagementModal;
