import React, { useState } from 'react';
import '../styles/Login.css';

function Login({ onLogin }) {
  const [username, setUsername] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');

    // Map usernames to user types
    const userTypeMap = {
      'super': 'super',
      'admin': 'admin',
      'appadmin': 'app_admin',
      'enduser': 'end'
    };

    const userType = userTypeMap[username.toLowerCase()];

    if (userType) {
      onLogin(userType);
    } else {
      setError('Invalid username. Please try again.');
    }
  };

  return (
    <div className="login-container">
      <h2>Secrets Management System</h2>
      <form onSubmit={handleSubmit} className="login-form">
        <div className="form-group">
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Enter your username"
            required
          />
        </div>
        {error && <div className="error-message">{error}</div>}
        <button type="submit">Login</button>
      </form>
      <div className="login-help">
        <p>Available usernames:</p>
        <ul>
          <li>super - Super User Dashboard</li>
          <li>admin - Admin Dashboard</li>
          <li>appadmin - App Admin Dashboard</li>
          <li>enduser - End User Dashboard</li>
        </ul>
      </div>
    </div>
  );
}

export default Login;
