import React, { useEffect, useState } from 'react';
import { api } from '../services/api';
import '../styles/AppAdminManagement.css';

function AppAdminManagement({ onLogout }) {
  const [applications, setApplications] = useState([]);
  const [newAdmin, setNewAdmin] = useState({
    username: '',
    email: '',
    selectedApps: []
  });
  const [appAdmins, setAppAdmins] = useState([]);
  const [loadingAppAdmins, setLoadingAppAdmins] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  useEffect(() => {
    fetchApplications();
    fetchAppAdmins();
  }, []);

  const fetchApplications = async () => {
    try {
      const data = await api.getApplications();
      setApplications(data);
    } catch (err) {
      setError('Failed to fetch applications');
    }
  };

  const fetchAppAdmins = async () => {
    try {
      setLoadingAppAdmins(true);
      console.log('Fetching app admins...');

      // Replace with your actual API call to fetch app admins:
      const data = await api.get('/api/appadmins'); // Assuming api.get is available and the endpoint is /api/appadmins

      // For now, keeping dummy data for structure, but this should be replaced
      // const dummyAppAdmins = [
      //     { user_id: 1, username: 'appadmin1', email: 'appadmin1@example.com', applications: [{ app_name: 'App A' }, { app_name: 'App B' }] },
      //     { user_id: 2, username: 'appadmin2', email: 'appadmin2@example.com', applications: [{ app_name: 'App C' }] },
      // ];
      setAppAdmins(data); // Use data from API call

      setLoadingAppAdmins(false);
      setError(null); // Clear any previous error on successful fetch
    } catch (err) {
      console.error('Error fetching app admins:', err); // Improved logging
      setError(`Failed to fetch app admin data: ${err.message || err}`); // Display error message
      setLoadingAppAdmins(false);
    }
  };

  const handleAddAppAdmin = async (e) => {
    e.preventDefault();
    if (!newAdmin.username || !newAdmin.email || newAdmin.selectedApps.length === 0) {
      setError('Please fill in all fields and select at least one application');
      return;
    }

    try {
      const userData = {
        username: newAdmin.username,
        email: newAdmin.email,
        password_hash: 'defaultPassword123',
        user_type: 'app_admin'
      };

      const createdUser = await api.createUser(userData);

      const assignmentPromises = newAdmin.selectedApps.map(appId => 
        api.assignAppAdmin(createdUser.user_id, appId)
      );
      await Promise.all(assignmentPromises);

      setSuccess('App admin added successfully with access to selected applications');
      setNewAdmin({ username: '', email: '', selectedApps: [] });
      fetchAppAdmins();
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error adding app admin:', err);
      setError('Failed to add app admin. Please try again.');
      setTimeout(() => setError(null), 3000);
    }
  };

  const handleAppSelection = (appId) => {
    setNewAdmin(prev => {
      const isSelected = prev.selectedApps.includes(appId);
      return {
        ...prev,
        selectedApps: isSelected
          ? prev.selectedApps.filter(id => id !== appId)
          : [...prev.selectedApps, appId]
      };
    });
  };

  return (
    <div className="app-admin-management">
      <h2>App Admin Management</h2>

      {error && <div className="error-message">{error}</div>}
      {success && <div className="success-message">{success}</div>}

      <div className="add-admin-section">
        <h3>Add New App Admin</h3>
        <form onSubmit={handleAddAppAdmin}>
          <div className="form-group">
            <input
              type="text"
              value={newAdmin.username}
              onChange={(e) => setNewAdmin({ ...newAdmin, username: e.target.value })}
              placeholder="Username"
              className="input-field"
            />
          </div>
          <div className="form-group">
            <input
              type="email"
              value={newAdmin.email}
              onChange={(e) => setNewAdmin({ ...newAdmin, email: e.target.value })}
              placeholder="Email"
              className="input-field"
            />
          </div>
          <div className="form-group">
            <label className="applications-label">Select Applications:</label>
            <div className="applications-grid">
              {applications.map(app => (
                <div key={app.app_id} className="application-checkbox">
                  <input
                    type="checkbox"
                    id={`app-${app.app_id}`}
                    checked={newAdmin.selectedApps.includes(app.app_id)}
                    onChange={() => handleAppSelection(app.app_id)}
                  />
                  <label htmlFor={`app-${app.app_id}`}>{app.app_name}</label>
                </div>
              ))}
            </div>
          </div>
          <button type="submit" className="add-button">Add App Admin</button>
        </form>
      </div>

      <div className="app-admins-list-container">
        <h3>Existing App Admins</h3>
        {loadingAppAdmins ? (
          <div className="loading">Loading app admins...</div>
        ) : error ? (
          <div className="error">{error}</div>
        ) : (
          <table className="app-admins-table">
            <thead>
              <tr>
                <th>Username</th>
                <th>Email</th>
                <th>Applications</th>
              </tr>
            </thead>
            <tbody>
              {appAdmins.map(admin => (
                <tr key={admin.user_id}>
                  <td>{admin.username}</td>
                  <td>{admin.email}</td>
                  <td>
                    {admin.applications && admin.applications.length > 0 ? (
                      admin.applications.map((app, index) => (
                        <span key={index} className="app-admin-app-tag">
                          {app.app_name}
                        </span>
                      ))
                    ) : (
                      'No applications assigned'
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default AppAdminManagement;
