import React, { useEffect, useState } from 'react';
import '../styles/Login.css';

function Login({ onLogin }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [activeInput, setActiveInput] = useState(null);

  useEffect(() => {
    // Add animation class to form when component mounts
    const form = document.querySelector('.login-form');
    if (form) {
      form.classList.add('form-visible');
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    // Simulate API call with timeout
    setTimeout(() => {
      // Map usernames to user types
      const userTypeMap = {
        'super': 'super',
        'admin': 'admin',
        'appadmin': 'app_admin',
        'enduser': 'end'
      };

      const userType = userTypeMap[username.toLowerCase()];

      if (userType && password) {
        onLogin(userType);
      } else if (!password) {
        setError('Please enter a password.');
      } else {
        setError('Invalid username. Please try again.');
      }
      setIsLoading(false);
    }, 1000);
  };

  const handleDashboardSubmit = () => {
    setError('');

    // Map usernames to user types
    const userTypeMap = {
      'super': 'super',
      'admin': 'admin',
      'appadmin': 'app_admin',
      'enduser': 'end'
    };

    const userType = userTypeMap[username.toLowerCase()];

    if (userType) {
      onLogin(userType);
    } else {
      setError('Invalid username. Please try again.');
    }
  };

  const handleInputFocus = (inputName) => {
    setActiveInput(inputName);
  };

  const handleInputBlur = () => {
    setActiveInput(null);
  };

  return (
    <div className="login-container">
      <div className="login-content">
        <h2 className="login-title">
          <span className="title-icon">🔐</span>
          Secrets Management System
        </h2>
        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-fields">
            <div className={`form-group ${activeInput === 'username' ? 'active' : ''}`}>
              <label htmlFor="username">
                <span className="input-icon">👤</span>
                Username
              </label>
              <input
                type="text"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                onFocus={() => handleInputFocus('username')}
                onBlur={handleInputBlur}
                placeholder="Enter your username"
                required
              />
            </div>
            <div className={`form-group ${activeInput === 'password' ? 'active' : ''}`}>
              <label htmlFor="password">
                <span className="input-icon">🔑</span>
                Password
              </label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                onFocus={() => handleInputFocus('password')}
                onBlur={handleInputBlur}
                placeholder="Enter your password"
                required
              />
            </div>
          </div>
          {error && (
            <div className="error-message">
              <span className="error-icon">⚠️</span>
              {error}
            </div>
          )}
          <div className="submit-section">
            <button 
              type="submit" 
              className={`submit-button ${isLoading ? 'loading' : ''}`}
              disabled={isLoading}
            >
              {isLoading ? (
                <>
                  <span className="loading-spinner"></span>
                  Logging in...
                </>
              ) : (
                <>
                  <span className="button-icon">→</span>
                  Login
                </>
              )}
            </button>
          </div>
        </form>

        <div className="dashboard-section">
          <h3>Quick Access to Dashboard</h3>
          <p className="dashboard-note">Enter username and click below to go directly to your dashboard</p>
          <button 
            onClick={handleDashboardSubmit}
            className="dashboard-button"
          >
            <span className="dashboard-icon">🚀</span>
            Go to Dashboard
          </button>
        </div>

        <div className="login-help">
          <div className="help-header">
            <span className="help-icon">💡</span>
            Available usernames:
          </div>
          <ul className="username-list">
            <li>
              <span className="role-icon">👑</span>
              super - Super User Dashboard
            </li>
            <li>
              <span className="role-icon">⚡</span>
              admin - Admin Dashboard
            </li>
            <li>
              <span className="role-icon">🔧</span>
              appadmin - App Admin Dashboard
            </li>
            <li>
              <span className="role-icon">👥</span>
              enduser - End User Dashboard
            </li>
          </ul>
          <p className="password-note">
            <span className="note-icon">ℹ️</span>
            Note: Any password will work for demo purposes
          </p>
        </div>
      </div>
    </div>
  );
}

export default Login;
