import React from 'react';
import { Navigate, Route, BrowserRouter as Router, Routes } from 'react-router-dom';
import './App.css';

// App components
const DoverPortal = () => <div className="portal">Welcome to Dover Portal</div>;
const GitHubPortal = () => <div className="portal">Welcome to GitHub Portal</div>;
const HRPortal = () => <div className="portal">Access Denied: You don't have permission to access HR Portal</div>;
const DevOpsPortal = () => <div className="portal">Access Denied: You don't have permission to access DevOps Portal</div>;
const FinancePortal = () => <div className="portal">Access Denied: You don't have permission to access Finance Portal</div>;

// Navigation component
const Navigation = () => (
  <nav className="nav-menu">
    <ul>
      <li><a href="/dover">Dover Portal</a></li>
      <li><a href="/github">GitHub Portal</a></li>
      <li><a href="/hr">HR Portal</a></li>
      <li><a href="/devops">DevOps Portal</a></li>
      <li><a href="/finance">Finance Portal</a></li>
    </ul>
  </nav>
);

function App() {
  return (
    <Router>
      <div className="App">
        <h1>Portal Access Demo</h1>
        <Navigation />
        <div className="content">
          <Routes>
            <Route path="/" element={<Navigate to="/dover" />} />
            <Route path="/dover" element={<DoverPortal />} />
            <Route path="/github" element={<GitHubPortal />} />
            <Route path="/hr" element={<HRPortal />} />
            <Route path="/devops" element={<DevOpsPortal />} />
            <Route path="/finance" element={<FinancePortal />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
