import React, { useEffect, useState } from 'react';
import { api } from '../services/api';
import '../styles/AppAdminManagement.css';

function AppAdminManagement({ onLogout }) {
  const [applications, setApplications] = useState([]);
  const [newAdmin, setNewAdmin] = useState({
    username: '',
    email: '',
    selectedApp: ''
  });
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  useEffect(() => {
    fetchApplications();
  }, []);

  const fetchApplications = async () => {
    try {
      const data = await api.getApplications();
      setApplications(data);
    } catch (err) {
      setError('Failed to fetch applications');
    }
  };

  const handleAddAppAdmin = async (e) => {
    e.preventDefault();
    if (!newAdmin.username || !newAdmin.email || !newAdmin.selectedApp) {
      setError('Please fill in all fields');
      return;
    }

    try {
      // First create the user with a default password
      const userData = {
        username: newAdmin.username,
        email: newAdmin.email,
        password_hash: 'defaultPassword123', // This will be hashed on the server
        user_type: 'app_admin'
      };

      const createdUser = await api.createUser(userData);

      // Then assign them to the selected application
      await api.assignAppAdmin(createdUser.user_id, newAdmin.selectedApp);

      setSuccess('App admin added successfully');
      setNewAdmin({ username: '', email: '', selectedApp: '' });
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error adding app admin:', err);
      setError('Failed to add app admin. Please try again.');
      setTimeout(() => setError(null), 3000);
    }
  };

  return (
    <div className="app-admin-management">
      <h2>App Admin Management</h2>

      {error && <div className="error-message">{error}</div>}
      {success && <div className="success-message">{success}</div>}

      <div className="add-admin-section">
        <h3>Add New App Admin</h3>
        <form onSubmit={handleAddAppAdmin}>
          <div className="form-group">
            <input
              type="text"
              value={newAdmin.username}
              onChange={(e) => setNewAdmin({ ...newAdmin, username: e.target.value })}
              placeholder="Username"
              className="input-field"
            />
          </div>
          <div className="form-group">
            <input
              type="email"
              value={newAdmin.email}
              onChange={(e) => setNewAdmin({ ...newAdmin, email: e.target.value })}
              placeholder="Email"
              className="input-field"
            />
          </div>
          <div className="form-group">
            <select
              value={newAdmin.selectedApp}
              onChange={(e) => setNewAdmin({ ...newAdmin, selectedApp: e.target.value })}
              className="select-field"
            >
              <option value="">Select Application</option>
              {applications.map(app => (
                <option key={app.app_id} value={app.app_id}>
                  {app.app_name}
                </option>
              ))}
            </select>
          </div>
          <button type="submit" className="add-button">Add App Admin</button>
        </form>
      </div>
    </div>
  );
}

export default AppAdminManagement;
