import React, { useCallback, useEffect, useState } from 'react';
import { api } from '../services/api';
import '../styles/ApplicationList.css';
import AppManagementModal from './AppManagementModal';

// GitHub logo SVG component
const GitHubLogo = () => (
  <svg height="24" viewBox="0 0 16 16" width="24" fill="currentColor">
    <path d="M8 0C3.58 0 0 3.58 0 8c0 3.54 2.29 6.53 5.47 7.59.4.07.55-.17.55-.38 0-.19-.01-.82-.01-1.49-2.01.37-2.53-.49-2.69-.94-.09-.23-.48-.94-.82-1.13-.28-.15-.68-.52-.01-.53.63-.01 1.08.58 1.23.82.72 1.21 1.87.87 2.33.66.07-.52.28-.87.51-1.07-1.78-.2-3.64-.89-3.64-3.95 0-.87.31-1.59.82-2.15-.08-.2-.36-1.02.08-2.12 0 0 .67-.21 2.2.82.64-.18 1.32-.27 2-.27.68 0 1.36.09 2 .27 1.53-1.04 2.2-.82 2.2-.82.44 1.1.16 1.92.08 2.12.51.56.82 1.27.82 2.15 0 3.07-1.87 3.75-3.65 3.95.29.25.54.73.54 1.48 0 1.07-.01 1.93-.01 2.2 0 .21.15.46.55.38A8.013 8.013 0 0016 8c0-4.42-3.58-8-8-8z"></path>
  </svg>
);

function ApplicationList({ userType }) {
  const [applications, setApplications] = useState(() => {
    // Try to load applications from localStorage first
    const savedApplications = localStorage.getItem('applications');
    return savedApplications ? JSON.parse(savedApplications) : [];
  });
  const [newApp, setNewApp] = useState({ name: '', url: '', roles: '' });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [selectedApp, setSelectedApp] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredApplications, setFilteredApplications] = useState([]);
  const [showAddModal, setShowAddModal] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true); // Set loading to true before fetching
      try {
        const appsData = await api.getApplications();

        // Ensure roles are consistently handled as objects
        const processedApps = appsData.map(app => ({
          ...app,
          roles: (app.roles || []).map(role => 
            typeof role === 'object' ? role : { role_id: role, role_name: role }
          )
        }));

        setApplications(processedApps);

        // Filter applications based on user type
        if (userType === 'end') {
          const filteredApps = processedApps.filter(app => 
            !app.app_name.toLowerCase().includes('admin') &&
            !app.app_name.toLowerCase().includes('finance') &&
            !app.app_name.toLowerCase().includes('hr') &&
            !app.app_name.toLowerCase().includes('devops')
          );
          setFilteredApplications(filteredApps);
        } else {
          setFilteredApplications(processedApps);
        }
      } catch (err) {
        console.error('Error fetching applications:', err);
        setError('Failed to fetch applications');
      } finally {
        setLoading(false); // Set loading to false after fetch completes (success or error)
      }
    };

    fetchData();
  }, [userType]);

  // Save applications to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('applications', JSON.stringify(applications));
  }, [applications]);

  useEffect(() => {
    const lowerCaseQuery = searchQuery.toLowerCase();
    let filtered = applications;

    // For end users, show all applications except admin-specific ones and DevOps
    if (userType === 'end') {
      filtered = applications.filter(app => 
        !app.app_name.toLowerCase().includes('admin') &&
        !app.app_name.toLowerCase().includes('finance') &&
        !app.app_name.toLowerCase().includes('hr') &&
        !app.app_name.toLowerCase().includes('devops')
      );
    }

    // Apply search filter
    filtered = filtered.filter(app =>
      app.app_name.toLowerCase().includes(lowerCaseQuery)
    );

    setFilteredApplications(filtered);
  }, [applications, searchQuery, userType]);

  const handleAddApplication = async () => {
    if (newApp.name && newApp.url) {
      try {
        // Parse roles from comma-separated string
        const roles = newApp.roles
          ? newApp.roles.split(',').map(role => role.trim()).filter(role => role)
          : [];

        const application = {
          app_name: newApp.name,
          app_url: newApp.url,
          description: newApp.roles,
          roles: roles,
          created_by: 2 // This should come from the logged-in user
        };

        await api.createApplication(application);
        const newApplication = {
          ...application,
          roles: roles
        };

        setApplications(prevApps => [...prevApps, newApplication]);
        setNewApp({ name: '', url: '', roles: '' });
        setSuccess('Application added successfully');
        setTimeout(() => setSuccess(null), 3000);
      } catch (err) {
        setError(`Failed to add application: ${err.message}`);
      }
    } else {
      setError('Please fill in all required fields');
    }
  };

  const handleAccessApp = (app) => {
    // For end users, block access to admin-specific applications and DevOps
    if (userType === 'end' && 
        (app.app_name.toLowerCase().includes('admin') ||
         app.app_name.toLowerCase().includes('finance') ||
         app.app_name.toLowerCase().includes('hr') ||
         app.app_name.toLowerCase().includes('devops'))) {
      setError('Access denied: This application is restricted for end users');
      return;
    }

    // Format the URL properly
    let url = app.app_url;

    // If the URL is just a domain (e.g., "youtube.com"), add https://
    if (!url.includes('://')) {
      url = 'https://' + url;
    }

    // If the URL contains our local server address, extract the actual application URL
    if (url.includes('192.168.1.64:3002')) {
      const parts = url.split('/');
      const lastPart = parts[parts.length - 1];
      url = 'https://' + lastPart;
    }

    window.open(url, '_blank');
  };

  const handleManageApp = (app) => {
    setSelectedApp(app);
  };

  const handleCloseModal = () => {
    setSelectedApp(null);
  };

  const handleAppUpdate = async (type, data) => {
    try {
      if (type === 'settings' && data.save) {
        // Update application settings
        const updatedApp = {
          app_name: data.name || selectedApp.app_name,
          app_url: data.url || selectedApp.app_url,
          description: data.description || selectedApp.description
        };

        await api.updateApplication(selectedApp.app_id, updatedApp);

        // Fetch updated applications to get the latest roles
        const updatedAppsData = await api.getApplications();
        const processedApps = updatedAppsData.map(app => ({
          ...app,
          roles: (app.roles || []).map(role => 
            typeof role === 'object' ? role : { role_id: role, role_name: role }
          )
        }));

        setApplications(processedApps);
        setFilteredApplications(processedApps);
        setSuccess('Application settings updated successfully');
        handleCloseModal();
      } else if (type === 'role') {
        if (data.action === 'add') {
          // Create role with password
          if (!data.role || !data.password) {
            setError('Role name and password are required');
            return;
          }

          const roleData = {
            role_name: data.role,
            password: data.password,
            app_id: selectedApp.app_id
          };

          const response = await api.createRole(roleData);

          // Fetch updated applications to get the latest roles
          const updatedAppsData = await api.getApplications();
          const processedApps = updatedAppsData.map(app => ({
            ...app,
            roles: (app.roles || []).map(role => 
              typeof role === 'object' ? role : { role_id: role, role_name: role }
            )
          }));

          setApplications(processedApps);
          setFilteredApplications(processedApps);
          setSuccess('Role added successfully');
        } else if (data.action === 'remove') {
          // Get the role ID from the role name
          const roleToDelete = selectedApp.roles.find(role => 
            (typeof role === 'object' ? role.role_name : role) === data.role
          );
          if (!roleToDelete) {
            setError('Role not found');
            return;
          }

          await api.deleteRole(typeof roleToDelete === 'object' ? roleToDelete.role_id : roleToDelete);

          // Fetch updated applications to get the latest roles
          const updatedAppsData = await api.getApplications();
          const processedApps = updatedAppsData.map(app => ({
            ...app,
            roles: (app.roles || []).map(role => 
              typeof role === 'object' ? role : { role_id: role, role_name: role }
            )
          }));

          setApplications(processedApps);
          setFilteredApplications(processedApps);
          setSuccess('Role removed successfully');
        }
      } else if (type === 'user') {
        setSuccess('User assigned successfully');
      }
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error updating application:', err);
      setError(`Failed to update application: ${err.response?.data?.message || err.message}`);
      setTimeout(() => setError(null), 3000);
    }
  };

  const handleDeleteApplication = async (appId) => {
    if (window.confirm('Are you sure you want to delete this application? This action cannot be undone.')) {
      try {
        await api.deleteApplication(appId);
        setApplications(applications.filter(app => app.app_id !== appId));
        setSuccess('Application deleted successfully');
        setTimeout(() => setSuccess(null), 3000);
      } catch (err) {
        console.error('Error deleting application:', err);
        setError(`Failed to delete application: ${err.message}`);
      }
    }
  };

  const getAppIcon = (appName) => {
    const name = appName.toLowerCase();
    if (name.includes('github')) return <GitHubLogo />;
    if (name.includes('jira')) return '🎯';
    if (name.includes('slack')) return '💬';
    if (name.includes('confluence')) return '📚';
    if (name.includes('jenkins')) return '⚙️';
    if (name.includes('aws')) return '☁️';
    if (name.includes('azure')) return '🌩️';
    if (name.includes('gcp')) return '☁️';
    if (name.includes('docker')) return '🐳';
    if (name.includes('kubernetes')) return '⚓';
    if (name.includes('gitlab')) return '🦊';
    if (name.includes('bitbucket')) return '🐙';
    if (name.includes('trello')) return '📋';
    if (name.includes('figma')) return '🎨';
    if (name.includes('zoom')) return '🎥';
    if (name.includes('teams')) return '👥';
    if (name.includes('hr')) return '👥';
    if (name.includes('finance')) return '💰';
    if (name.includes('devops')) return '🛠️';
    return '🔗'; // Default icon
  };

  const handleSearch = useCallback((query) => {
    setSearchQuery(query);
  }, []);

  if (loading) return <div className="loading">Loading applications...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="application-list">
      <div className="header">
        <h2>Applications</h2>
        {userType === 'super' && (
          <button className="add-button" onClick={() => setShowAddModal(true)}>
            Add Application
          </button>
        )}
      </div>

      {error && <div className="error-message">{error}</div>}
      {success && <div className="success-message">{success}</div>}

      <div className="search-bar">
        <input
          type="text"
          placeholder="Search applications..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
      </div>

      {loading ? (
        <div className="loading">Loading applications...</div>
      ) : filteredApplications.length === 0 ? (
        <div className="no-applications">
          {userType === 'end' ? (
            <p>No applications available for your access level.</p>
          ) : (
            <p>No applications found. {userType === 'super' && 'Click "Add Application" to create one.'}</p>
          )}
        </div>
      ) : (
        <div className="applications-table-container">
          <table className="applications-table">
            <thead>
              <tr>
                <th>Application</th>
                <th>Roles</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredApplications.map((app) => (
                <tr key={app.app_id}>
                  <td>
                    <div className="app-info">
                      <span className="app-name">{app.app_name}</span>
                      {app.description && (
                        <span className="app-description">{app.description}</span>
                      )}
                    </div>
                  </td>
                  <td className="roles-cell">
                    {app.roles && app.roles.length > 0 ? (
                      <div className="roles-list">
                        {app.roles.map((role, index) => (
                          <span key={index} className="role-tag">
                            {typeof role === 'object' ? role.role_name : role}
                          </span>
                        ))}
                      </div>
                    ) : (
                      <span className="no-roles">No roles assigned</span>
                    )}
                  </td>
                  <td className="app-actions">
                    <div className="action-buttons">
                      <button
                        className="access-button"
                        onClick={() => handleAccessApp(app)}
                      >
                        Access
                      </button>
                      {(userType === 'super' || userType === 'app_admin') && (
                        <button
                          className="manage-button"
                          onClick={() => handleManageApp(app)}
                        >
                          Manage
                        </button>
                      )}
                      {userType === 'super' && (
                        <button
                          className="delete-button"
                          onClick={() => handleDeleteApplication(app.app_id)}
                        >
                          Delete
                        </button>
                      )}
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {showAddModal && (
        <AppManagementModal
          app={null}
          userType={userType}
          onClose={() => setShowAddModal(false)}
          onUpdate={handleAppUpdate}
          isAdminOnly={false}
        />
      )}

      {selectedApp && (
        <AppManagementModal
          app={selectedApp}
          userType={userType}
          onClose={handleCloseModal}
          onUpdate={handleAppUpdate}
          isAdminOnly={false}
        />
      )}
    </div>
  );
}

export default ApplicationList;
