import React, { useEffect, useState } from 'react';
import { api } from '../services/api';
import '../styles/ApplicationList.css';
import AppManagementModal from './AppManagementModal';

// GitHub logo SVG component
const GitHubLogo = () => (
  <svg height="24" viewBox="0 0 16 16" width="24" fill="currentColor">
    <path d="M8 0C3.58 0 0 3.58 0 8c0 3.54 2.29 6.53 5.47 7.59.4.07.55-.17.55-.38 0-.19-.01-.82-.01-1.49-2.01.37-2.53-.49-2.69-.94-.09-.23-.48-.94-.82-1.13-.28-.15-.68-.52-.01-.53.63-.01 1.08.58 1.23.82.72 1.21 1.87.87 2.33.66.07-.52.28-.87.51-1.07-1.78-.2-3.64-.89-3.64-3.95 0-.87.31-1.59.82-2.15-.08-.2-.36-1.02.08-2.12 0 0 .67-.21 2.2.82.64-.18 1.32-.27 2-.27.68 0 1.36.09 2 .27 1.53-1.04 2.2-.82 2.2-.82.44 1.1.16 1.92.08 2.12.51.56.82 1.27.82 2.15 0 3.07-1.87 3.75-3.65 3.95.29.25.54.73.54 1.48 0 1.07-.01 1.93-.01 2.2 0 .21.15.46.55.38A8.013 8.013 0 0016 8c0-4.42-3.58-8-8-8z"></path>
  </svg>
);

function ApplicationList({ userType }) {
  const [applications, setApplications] = useState([]);
  const [newApp, setNewApp] = useState({ name: '', url: '', roles: '' });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [selectedApp, setSelectedApp] = useState(null);
  const [restrictedApps, setRestrictedApps] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredApplications, setFilteredApplications] = useState([]);

  useEffect(() => {
    fetchApplications();
  }, []);

  useEffect(() => {
    if (userType === 'end_user') {
      // Logic for end users to fetch and filter restricted apps
      const fetchRestrictedApps = async () => {
        try {
          const data = await api.getApplications(); // Re-fetch or use passed applications
          const restricted = data.filter(app => {
            const appName = app.app_name.toLowerCase();
            return appName.includes('hr') || 
                   appName.includes('devops') || 
                   appName.includes('finance');
          });
          setRestrictedApps(restricted);
        } catch (err) {
          console.error('Error fetching restricted applications:', err);
        }
      };
      // If applications are passed, use them, otherwise fetch
      if (applications && applications.length > 0) {
         const restricted = applications.filter(app => {
            const appName = app.app_name.toLowerCase();
            return appName.includes('hr') || 
                   appName.includes('devops') || 
                   appName.includes('finance');
          });
          setRestrictedApps(restricted);
      } else {
         fetchRestrictedApps();
      }

    } else if (userType === 'app_admin' || userType === 'admin') {
       setFilteredApplications(applications || []);
    }
  }, [applications, userType]);

  useEffect(() => {
    if (userType === 'app_admin' || userType === 'admin') {
      const lowerCaseQuery = searchQuery.toLowerCase();
      const filtered = applications.filter(app => 
        app.app_name.toLowerCase().includes(lowerCaseQuery) ||
        app.app_url.toLowerCase().includes(lowerCaseQuery) ||
        app.description?.toLowerCase().includes(lowerCaseQuery)
      );
      setFilteredApplications(filtered);
    }
  }, [searchQuery, applications, userType]);

  const fetchApplications = async () => {
    try {
      console.log('Fetching applications...');
      const data = await api.getApplications();
      console.log('Received applications:', data);
      setApplications(data);

      // Set restricted apps for end users
      if (userType === 'end') {
        const restricted = data.filter(app => {
          const appName = app.app_name.toLowerCase();
          return appName.includes('hr') || 
                 appName.includes('devops') || 
                 appName.includes('finance');
        });
        setRestrictedApps(restricted.map(app => app.app_id));
      }

      setLoading(false);
    } catch (err) {
      console.error('Error fetching applications:', err);
      setError(`Failed to fetch applications: ${err.message}`);
      setLoading(false);
    }
  };

  const handleAddApplication = async () => {
    if (newApp.name && newApp.url) {
      try {
        const application = {
          app_name: newApp.name,
          app_url: newApp.url,
          description: newApp.roles,
          created_by: 2 // This should come from the logged-in user
        };

        const result = await api.createApplication(application);
        setApplications([...applications, result]);
        setNewApp({ name: '', url: '', roles: '' });
        setSuccess('Application added successfully');
        setTimeout(() => setSuccess(null), 3000);
      } catch (err) {
        console.error('Error adding application:', err);
        setError(`Failed to add application: ${err.message}`);
      }
    } else {
      setError('Please fill in all required fields');
    }
  };

  const handleRemoveRole = async (appId, roleToRemove) => {
    try {
      // This would be an API call to remove a role
      const updatedApplications = applications.map(app =>
        app.app_id === appId
          ? { ...app, roles: (app.roles || []).filter(role => role !== roleToRemove) }
          : app
      );
      setApplications(updatedApplications);
      setSuccess('Role removed successfully');
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error removing role:', err);
      setError(`Failed to remove role: ${err.message}`);
    }
  };

  const handleAccessApp = async (app) => {
    if (userType === 'end' && restrictedApps.includes(app.app_id)) {
      setError(
        <div className="access-denied-message">
          <span>Access denied. Please request access to this application.</span>
          <button 
            className="back-to-apps-button"
            onClick={() => setError(null)}
          >
            Back to Applications
          </button>
        </div>
      );
      return;
    }

    try {
      // Check if this is the Github App (by name or URL)
      if (app.app_name.toLowerCase().includes('github') || app.app_url.includes('github.com')) {
        try {
          await api.triggerGithubAutoLogin();
        } catch (err) {
          console.error("Auto-login failed", err);
        }
      }

      // Open the app URL
      const formattedUrl = app.app_url.startsWith('http') ? app.app_url : `https://${app.app_url}`;
      window.open(formattedUrl, '_blank', 'noopener,noreferrer');
    } catch (err) {
      setError(`Failed to access application: ${err.message}`);
    }
  };

  const handleManageApp = (app) => {
    setSelectedApp(app);
  };

  const handleCloseModal = () => {
    setSelectedApp(null);
  };

  const handleAppUpdate = async (type, data) => {
    try {
      if (type === 'settings' && data.save) {
        // Update application settings
        const updatedApp = {
          app_name: data.name || selectedApp.app_name,
          app_url: data.url || selectedApp.app_url,
          description: data.description || selectedApp.description
        };

        await api.updateApplication(selectedApp.app_id, updatedApp);

        // Update the applications list with the new data
        const updatedApps = applications.map(app =>
          app.app_id === selectedApp.app_id
            ? { ...app, ...updatedApp }
            : app
        );
        setApplications(updatedApps);
        setSuccess('Application settings updated successfully');
        handleCloseModal();
      } else if (type === 'role') {
        if (data.action === 'add') {
          const updatedApps = applications.map(app =>
            app.app_id === selectedApp.app_id
              ? { ...app, roles: [...(app.roles || []), data.role] }
              : app
          );
          setApplications(updatedApps);
          setSuccess('Role added successfully');
        } else if (data.action === 'remove') {
          const updatedApps = applications.map(app =>
            app.app_id === selectedApp.app_id
              ? { ...app, roles: app.roles.filter(role => role !== data.role) }
              : app
          );
          setApplications(updatedApps);
          setSuccess('Role removed successfully');
        }
      } else if (type === 'user') {
        setSuccess('User assigned successfully');
      }
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error updating application:', err);
      setError(`Failed to update application: ${err.message}`);
    }
  };

  const handleDeleteApplication = async (appId) => {
    if (window.confirm('Are you sure you want to delete this application? This action cannot be undone.')) {
      try {
        await api.deleteApplication(appId);
        setApplications(applications.filter(app => app.app_id !== appId));
        setSuccess('Application deleted successfully');
        setTimeout(() => setSuccess(null), 3000);
      } catch (err) {
        console.error('Error deleting application:', err);
        setError(`Failed to delete application: ${err.message}`);
      }
    }
  };

  const getAppIcon = (appName) => {
    const name = appName.toLowerCase();
    if (name.includes('github')) return <GitHubLogo />;
    if (name.includes('jira')) return 'üéØ';
    if (name.includes('slack')) return 'üí¨';
    if (name.includes('confluence')) return 'üìö';
    if (name.includes('jenkins')) return '‚öôÔ∏è';
    if (name.includes('aws')) return '‚òÅÔ∏è';
    if (name.includes('azure')) return 'üå©Ô∏è';
    if (name.includes('gcp')) return '‚òÅÔ∏è';
    if (name.includes('docker')) return 'üê≥';
    if (name.includes('kubernetes')) return '‚öì';
    if (name.includes('gitlab')) return 'ü¶ä';
    if (name.includes('bitbucket')) return 'üêô';
    if (name.includes('trello')) return 'üìã';
    if (name.includes('figma')) return 'üé®';
    if (name.includes('zoom')) return 'üé•';
    if (name.includes('teams')) return 'üë•';
    if (name.includes('hr')) return 'üë•';
    if (name.includes('finance')) return 'üí∞';
    if (name.includes('devops')) return 'üõ†Ô∏è';
    return 'üîó'; // Default icon
  };

  if (loading) return <div className="loading">Loading applications...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="application-list-container">
      {/* Header section with title */}
      <div className="application-header-controls">
        <h3>Applications</h3>
      </div>

      {success && <div className="success-message">{success}</div>}
      {error && <div className="error-message">{error}</div>}

      {userType === 'admin' && (
        <div className="admin-controls">
          <div className="add-application-form">
            <h4>Add New Application</h4>
            <input
              type="text"
              value={newApp.name}
              onChange={(e) => setNewApp({ ...newApp, name: e.target.value })}
              placeholder="Application Name"
            />
            <input
              type="text"
              value={newApp.url}
              onChange={(e) => setNewApp({ ...newApp, url: e.target.value })}
              placeholder="Application URL (e.g., app.example.com)"
            />
            <button onClick={handleAddApplication}>Add Application</button>
          </div>

          {/* Admin search bar above the table */}
          <div className="application-search-container admin-search-container">
            <input
              type="text"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              placeholder="Search applications..."
              className="application-search-input admin-search-input"
            />
          </div>

          {/* Applications list as a table for admin */}
          <div className="applications-table-container">
            <table className="applications-table">
              <thead>
                <tr>
                  <th></th> {/* Icon column */}
                  <th>Application Name</th>
                  <th>Access</th>
                  <th>Manage</th>
                  <th>Delete</th>
                </tr>
              </thead>
              <tbody>
                {filteredApplications.map((app) => (
                  <tr key={app.app_id}>
                    <td>
                      <span className="app-icon table-icon">{getAppIcon(app.app_name)}</span>
                    </td>
                    <td>
                      <span className="app-name">{app.app_name}</span>
                    </td>
                    <td>
                      <button 
                        className={`access-button ${userType === 'end' && restrictedApps.includes(app.app_id) ? 'restricted' : ''}`}
                        onClick={() => handleAccessApp(app)}
                      >
                        {userType === 'end' && restrictedApps.includes(app.app_id) ? 'No Access' : 'Access'}
                      </button>
                    </td>
                    <td>
                      <button 
                        className="manage-button" 
                        onClick={() => handleManageApp(app)}
                      >
                        Manage
                      </button>
                    </td>
                    <td>
                      <button 
                        className="delete-button" 
                        onClick={() => handleDeleteApplication(app.app_id)}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {userType !== 'admin' && (
        <ul className="applications-list">
          {filteredApplications.map((app) => (
            <li key={app.app_id} className="application-item">
              <div className="app-info">
                <div className="app-header">
                  <span className="app-icon">{getAppIcon(app.app_name)}</span>
                  <span className="app-name">{app.app_name}</span>
                </div>
                {userType === 'app_admin' && (
                  <div className="app-roles">
                    <strong>Roles:</strong>
                    {(app.roles || []).map((role, index) => (
                      <span key={index} className="role-tag">
                        {role}
                        <button 
                          className="remove-role"
                          onClick={() => handleRemoveRole(app.app_id, role)}
                        >
                          √ó
                        </button>
                      </span>
                    ))}
                  </div>
                )}
              </div>
              <div className="app-actions">
                <button 
                  className={`access-button ${userType === 'end' && restrictedApps.includes(app.app_id) ? 'restricted' : ''}`}
                  onClick={() => handleAccessApp(app)}
                >
                  {userType === 'end' && restrictedApps.includes(app.app_id) ? 'No Access' : 'Access'}
                </button>
                {(userType === 'admin' || userType === 'app_admin') && (
                  <button 
                    className="manage-button" 
                    onClick={() => handleManageApp(app)}
                  >
                    Manage
                  </button>
                )}
                {userType === 'admin' && (
                  <button 
                    className="delete-button" 
                    onClick={() => handleDeleteApplication(app.app_id)}
                  >
                    Delete
                  </button>
                )}
              </div>
            </li>
          ))}
        </ul>
      )}

      {userType === 'app_admin' && (
        <div className="app-admin-info">
          <p>
            As an App Admin, you can assign users to your applications and manage their roles.
          </p>
        </div>
      )}

      {selectedApp && (
        <AppManagementModal
          app={selectedApp}
          userType={userType}
          onClose={handleCloseModal}
          onUpdate={handleAppUpdate}
          isAdminOnly={userType === 'admin'}
        />
      )}
    </div>
  );
}

export default ApplicationList;
