import React, { useEffect, useState } from 'react';
import { api } from '../services/api';
import '../styles/AccessRequests.css';

function AccessRequests() {
  const [accessRequests, setAccessRequests] = useState(() => {
    const savedRequests = localStorage.getItem('accessRequests');
    return savedRequests ? JSON.parse(savedRequests) : [];
  });
  const [applications, setApplications] = useState([]);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [filter, setFilter] = useState('all'); // 'all', 'pending', 'approved', 'denied'

  // Save access requests to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('accessRequests', JSON.stringify(accessRequests));
  }, [accessRequests]);

  useEffect(() => {
    fetchApplications();
  }, []);

  const fetchApplications = async () => {
    try {
      const data = await api.getApplications();
      setApplications(data);
    } catch (err) {
      setError('Failed to fetch applications');
    }
  };

  const handleApproveRequest = (requestId) => {
    try {
      const updatedRequests = accessRequests.map(request => {
        if (request.id === requestId) {
          return { ...request, status: 'approved' };
        }
        return request;
      });
      setAccessRequests(updatedRequests);
      setSuccess('Access request approved successfully');
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      setError('Failed to approve request');
      setTimeout(() => setError(null), 3000);
    }
  };

  const handleDenyRequest = (requestId) => {
    try {
      const updatedRequests = accessRequests.map(request => {
        if (request.id === requestId) {
          return { ...request, status: 'denied' };
        }
        return request;
      });
      setAccessRequests(updatedRequests);
      setSuccess('Access request denied');
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      setError('Failed to deny request');
      setTimeout(() => setError(null), 3000);
    }
  };

  const filteredRequests = accessRequests.filter(request => {
    if (filter === 'all') return true;
    return request.status === filter;
  });

  return (
    <div className="access-requests-container">
      <h2>Access Requests</h2>

      {error && <div className="error-message">{error}</div>}
      {success && <div className="success-message">{success}</div>}

      <div className="filter-controls">
        <button 
          className={`filter-button ${filter === 'all' ? 'active' : ''}`}
          onClick={() => setFilter('all')}
        >
          All
        </button>
        <button 
          className={`filter-button ${filter === 'pending' ? 'active' : ''}`}
          onClick={() => setFilter('pending')}
        >
          Pending
        </button>
        <button 
          className={`filter-button ${filter === 'approved' ? 'active' : ''}`}
          onClick={() => setFilter('approved')}
        >
          Approved
        </button>
        <button 
          className={`filter-button ${filter === 'denied' ? 'active' : ''}`}
          onClick={() => setFilter('denied')}
        >
          Denied
        </button>
      </div>

      <div className="requests-list">
        {filteredRequests.length === 0 ? (
          <p className="no-requests">No {filter} access requests</p>
        ) : (
          <table className="requests-table">
            <thead>
              <tr>
                <th>User</th>
                <th>Application</th>
                <th>Reason</th>
                <th>Requested At</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredRequests.map((request) => (
                <tr key={request.id}>
                  <td>{request.username}</td>
                  <td>{request.appName}</td>
                  <td>{request.reason}</td>
                  <td>{new Date(request.requestedAt).toLocaleString()}</td>
                  <td>
                    <span className={`status-badge ${request.status}`}>
                      {request.status}
                    </span>
                  </td>
                  <td>
                    {request.status === 'pending' && (
                      <div className="request-actions">
                        <button
                          className="approve-button"
                          onClick={() => handleApproveRequest(request.id)}
                        >
                          Approve
                        </button>
                        <button
                          className="deny-button"
                          onClick={() => handleDenyRequest(request.id)}
                        >
                          Deny
                        </button>
                      </div>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default AccessRequests;
