import {
    CategoryScale,
    Chart as ChartJS,
    Legend,
    LinearScale,
    LineElement,
    PointElement,
    Title,
    Tooltip,
} from 'chart.js';
import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import { api } from '../services/api';
import '../styles/UserAccessGraph.css';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

function UserAccessGraph() {
  const [accessData, setAccessData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const users = await api.getUsers();
        const userAppCounts = await Promise.all(
          users.map(async (user) => {
            const userApps = await api.getUserApplications(user.user_id);
            return {
              user: user.username,
              appCount: userApps.length
            };
          })
        );
        setAccessData(userAppCounts);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching user access data:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const chartData = {
    labels: accessData.map(item => item.user),
    datasets: [
      {
        label: 'Number of Applications',
        data: accessData.map(item => item.appCount),
        borderColor: 'rgb(75, 192, 192)',
        backgroundColor: 'rgba(75, 192, 192, 0.5)',
        tension: 0.4,
        pointRadius: 6,
        pointHoverRadius: 8,
      },
    ],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
        labels: {
          color: 'var(--text-color)',
          font: {
            size: 14,
          },
        },
      },
      title: {
        display: true,
        text: 'User Application Access Count',
        color: 'var(--text-color)',
        font: {
          size: 16,
          weight: 'bold',
        },
      },
      tooltip: {
        callbacks: {
          label: function(context) {
            return `Applications: ${context.raw}`;
          },
        },
      },
    },
    scales: {
      x: {
        ticks: {
          color: 'var(--text-color)',
          font: {
            size: 12,
          },
        },
        grid: {
          color: 'rgba(128, 128, 128, 0.2)',
        },
      },
      y: {
        ticks: {
          color: 'var(--text-color)',
          font: {
            size: 12,
          },
          stepSize: 1,
        },
        grid: {
          color: 'rgba(128, 128, 128, 0.2)',
        },
        beginAtZero: true,
      },
    },
  };

  if (loading) {
    return <div className="loading">Loading access data...</div>;
  }

  return (
    <div className="user-access-graph-container">
      <div className="graph-wrapper">
        <Line data={chartData} options={options} />
      </div>
    </div>
  );
}

export default UserAccessGraph;
