import React, { useEffect, useState } from 'react';
import { api } from '../services/api';
import '../styles/UserManagement.css';

function UserManagement({ isAppAdmin, onLogout }) {
  const [users, setUsers] = useState([]);
  const [newUser, setNewUser] = useState('');
  const [newUserEmail, setNewUserEmail] = useState('');
  const [selectedApp, setSelectedApp] = useState('');
  const [selectedRole, setSelectedRole] = useState('');
  const [applications, setApplications] = useState([]);
  const [roles, setRoles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [currentUser, setCurrentUser] = useState(null);

  const fetchData = async () => {
    try {
      setLoading(true);
      console.log('Fetching current user data...');
      const userData = await api.getCurrentUser();
      console.log('Current user data:', userData);
      setCurrentUser(userData);

      if (userData.user_type === 'app_admin') {
        console.log('Fetching app admin applications for user:', userData.user_id);
        const appAdminApps = await api.getAppAdminApplications(userData.user_id);
        console.log('App admin applications:', appAdminApps);
        setApplications(appAdminApps);
      } else {
        console.log('Fetching all users, applications, and roles...');
        try {
          const [usersData, appsData, rolesData] = await Promise.all([
            api.getUsers(),
            api.getApplications(),
            api.getRoles()
          ]);
          console.log('Users data:', usersData);
          console.log('Applications data:', appsData);
          console.log('Roles data:', rolesData);
          setUsers(usersData);
          setApplications(appsData);
          setRoles(rolesData);
        } catch (fetchError) {
          console.error('Error fetching data:', fetchError);
          if (fetchError.response) {
            console.error('Response data:', fetchError.response.data);
            console.error('Response status:', fetchError.response.status);
          }
          throw fetchError;
        }
      }
      setError(null);
    } catch (err) {
      console.error('Error in fetchData:', err);
      if (err.response) {
        console.error('Error response:', err.response.data);
        console.error('Error status:', err.response.status);
      }
      setError(`Failed to fetch data: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleAddUser = async () => {
    if (newUser.trim() && newUserEmail.trim()) {
      try {
        // First create the user
        const user = await api.createUser({
          username: newUser,
          email: newUserEmail,
          password_hash: 'defaultPassword123',
          user_type: 'end'
        });

        // If app admin and application is selected, assign the user to the application
        if (isAppAdmin && selectedApp && selectedRole) {
          await api.assignUserToApplication(user.user_id, selectedApp, selectedRole);
        }

        // Refresh the users list
        await fetchData();

        // Clear the form
        setNewUser('');
        setNewUserEmail('');
        setSelectedApp('');
        setSelectedRole('');

        setSuccess('User added successfully');
        setTimeout(() => setSuccess(null), 3000);
      } catch (err) {
        console.error('Error creating user:', err);
        setError('Failed to add user: ' + (err.response?.data?.message || err.message));
      }
    } else {
      setError('Please provide both username and email');
    }
  };

  const handleAssignApp = async (userId, appId, role) => {
    try {
      await api.assignUserToApplication(userId, appId, role);
      const updatedUsers = users.map(user => 
        user.id === userId 
          ? { 
              ...user, 
              assignedApps: [...user.assignedApps, { appId, role }] 
            } 
          : user
      );
      setUsers(updatedUsers);
      setSuccess('User assigned successfully');
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      setError('Failed to assign user');
    }
  };

  const handleRemoveAssignment = async (userId, appId) => {
    try {
      await api.removeUserFromApplication(userId, appId);
      const updatedUsers = users.map(user =>
        user.id === userId
          ? {
              ...user,
              assignedApps: user.assignedApps.filter(app => app.appId !== appId)
            }
          : user
      );
      setUsers(updatedUsers);
      setSuccess('Assignment removed successfully');
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      setError('Failed to remove assignment');
    }
  };

  const handleAddRole = async (appId, roleName) => {
    try {
      await api.createRole({
        app_id: appId,
        role_name: roleName
      });
      const updatedApps = applications.map(app =>
        app.id === appId
          ? { ...app, roles: [...app.roles, roleName] }
          : app
      );
      setApplications(updatedApps);
      setSuccess('Role added successfully');
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      setError('Failed to add role');
    }
  };

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="user-management-container">
      <div className="user-management-header">
        <h3>User Management</h3>
        <button className="back-button" onClick={onLogout}>Sign Out</button>
      </div>
      {success && <div className="success-message">{success}</div>}
      {error && <div className="error-message">{error}</div>}

      <div className="add-user-form">
        <input
          type="text"
          value={newUser}
          onChange={(e) => setNewUser(e.target.value)}
          placeholder="Enter new user name"
        />
        <input
          type="email"
          value={newUserEmail}
          onChange={(e) => setNewUserEmail(e.target.value)}
          placeholder="Enter user email"
        />
        {isAppAdmin && (
          <>
            <select
              value={selectedApp}
              onChange={(e) => {
                setSelectedApp(e.target.value);
                setSelectedRole(''); // Reset role when app changes
              }}
            >
              <option value="">Select Application</option>
              {applications.map(app => (
                <option key={app.id} value={app.id}>
                  {app.app_name}
                </option>
              ))}
            </select>
            <select
              value={selectedRole}
              onChange={(e) => setSelectedRole(e.target.value)}
              disabled={!selectedApp}
            >
              <option value="">Select Role</option>
              {selectedApp && applications
                .find(app => app.id === selectedApp)?.roles
                .map(role => (
                  <option key={role} value={role}>
                    {role}
                  </option>
                ))}
            </select>
          </>
        )}
        <button 
          onClick={handleAddUser}
          disabled={isAppAdmin && (!selectedApp || !selectedRole)}
        >
          Add {isAppAdmin ? 'End User' : 'App Admin'}
        </button>
      </div>

      {!isAppAdmin && (
        <div className="users-list">
          <h4>Current Users</h4>
          <ul>
            {users.map((user) => (
              <li key={user.user_id}>
                <div className="user-info">
                  <span className="user-name">{user.username}</span>
                  <span className="user-email">{user.email}</span>
                  <span className="user-type">{user.user_type}</span>
                  <div className="user-assignments">
                    <strong>Assigned Applications:</strong>
                    {user.assignedApps?.length > 0 ? (
                      <div className="assignments-list">
                        {user.assignedApps.map((assignment, index) => (
                          <div key={index} className="assignment-tag">
                            <span className="app-name">{assignment.appName || 'Unknown App'}</span>
                            <span className="role-name">({assignment.role || 'No Role'})</span>
                            <button 
                              className="remove-assignment"
                              onClick={() => handleRemoveAssignment(user.user_id, assignment.appId)}
                              title="Remove Assignment"
                            >
                              ×
                            </button>
                          </div>
                        ))}
                      </div>
                    ) : (
                      <span className="no-assignments">No applications assigned</span>
                    )}
                  </div>
                </div>
                <div className="assignment-controls">
                  <select
                    onChange={(e) => {
                      const [appId, role] = e.target.value.split('|');
                      handleAssignApp(user.user_id, appId, role);
                      e.target.value = '';
                    }}
                    defaultValue=""
                  >
                    <option value="" disabled>Assign to Application</option>
                    {applications.map(app => (
                      (app.roles || []).map(role => (
                        <option 
                          key={`${app.id}-${role}`} 
                          value={`${app.id}|${role}`}
                        >
                          {app.app_name} - {role}
                        </option>
                      ))
                    ))}
                  </select>
                </div>
              </li>
            ))}
          </ul>
        </div>
      )}

      {isAppAdmin && (
        <div className="role-management">
          <h4>Manage Roles</h4>
          {applications.map(app => (
            <div key={app.id} className="app-roles">
              <h5>{app.app_name}</h5>
              <div className="add-role-form">
                <input
                  type="text"
                  placeholder="New role name"
                  onKeyPress={(e) => {
                    if (e.key === 'Enter') {
                      handleAddRole(app.id, e.target.value);
                      e.target.value = '';
                    }
                  }}
                />
              </div>
              <div className="role-list">
                {(app.roles || []).map((role, index) => (
                  <span key={index} className="role-tag">{role}</span>
                ))}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default UserManagement;
