import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const Login = () => {
  const [credentials, setCredentials] = useState({
    username: '',
    password: '',
    role: ''
  });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleRoleSelect = (role) => {
    setCredentials(prev => ({
      ...prev,
      role
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      if (credentials.username && credentials.password && credentials.role) {
        // Store authentication and role information
        localStorage.setItem('isAuthenticated', 'true');
        localStorage.setItem('userRole', credentials.role);
        navigate('/dashboard');
      } else {
        setError('Please enter both username and password and select a role');
      }
    } catch (err) {
      setError('Login failed. Please try again.');
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h1>Secret Management System</h1>
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              name="username"
              value={credentials.username}
              onChange={handleChange}
              placeholder="Enter your username"
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={credentials.password}
              onChange={handleChange}
              placeholder="Enter your password"
              required
            />
          </div>
          <div className="form-group">
            <label>Select Role</label>
            <div className="role-buttons">
              <button
                type="button"
                className={`role-button ${credentials.role === 'super' ? 'active' : ''}`}
                onClick={() => handleRoleSelect('super')}
              >
                Super User
              </button>
              <button
                type="button"
                className={`role-button ${credentials.role === 'admin' ? 'active' : ''}`}
                onClick={() => handleRoleSelect('admin')}
              >
                Admin
              </button>
              <button
                type="button"
                className={`role-button ${credentials.role === 'app_admin' ? 'active' : ''}`}
                onClick={() => handleRoleSelect('app_admin')}
              >
                App Admin
              </button>
              <button
                type="button"
                className={`role-button ${credentials.role === 'end' ? 'active' : ''}`}
                onClick={() => handleRoleSelect('end')}
              >
                End User
              </button>
            </div>
          </div>
          {error && <div className="error-message">{error}</div>}
          <button type="submit" className="login-button">
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
