import React, { useEffect, useState } from 'react';
import { api } from '../services/api';
import '../styles/ApplicationList.css';

function ApplicationList({ userType }) {
  const [applications, setApplications] = useState([]);
  const [users, setUsers] = useState([]);
  const [appAssignments, setAppAssignments] = useState({});
  const [newApp, setNewApp] = useState({ name: '', url: '', roles: '' });
  const [newRole, setNewRole] = useState({ appId: '', roleName: '' });
  const [selectedApp, setSelectedApp] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  useEffect(() => {
    fetchApplications();
    if (userType === 'app_admin') {
      fetchUsers();
    }
  }, [userType]);

  const fetchApplications = async () => {
    try {
      console.log('Fetching applications...');
      const data = await api.getApplications();
      console.log('Received applications:', data);
      setApplications(data);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching applications:', err);
      setError(`Failed to fetch applications: ${err.message}`);
      setLoading(false);
    }
  };

  const fetchUsers = async () => {
    try {
      const data = await api.getUsers();
      // Filter users that are app_admin or have no user_type set
      setUsers(data.filter(user => !user.user_type || user.user_type === 'app_admin'));
    } catch (err) {
      console.error('Error fetching users:', err);
      setError(`Failed to fetch users: ${err.message}`);
    }
  };

  const fetchAppAssignments = async (appId) => {
    try {
      const assignments = await api.getAppAdminUsers(appId);
      setAppAssignments(prev => ({
        ...prev,
        [appId]: assignments
      }));
    } catch (err) {
      console.error('Error fetching assignments:', err);
      setError(`Failed to fetch assignments: ${err.message}`);
    }
  };

  const handleAddApplication = async () => {
    if (newApp.name && newApp.url) {
      try {
        const application = {
          app_name: newApp.name,
          app_url: newApp.url,
          description: newApp.roles,
          created_by: 2 // This should come from the logged-in user
        };

        const result = await api.createApplication(application);
        setApplications([...applications, result]);
        setNewApp({ name: '', url: '', roles: '' });
        setSuccess('Application added successfully');
        setTimeout(() => setSuccess(null), 3000);
      } catch (err) {
        console.error('Error adding application:', err);
        setError(`Failed to add application: ${err.message}`);
      }
    } else {
      setError('Please fill in all required fields');
    }
  };

  const handleAddRole = async (appId, roleName) => {
    if (!appId || !roleName) {
      setError('Please select an application and enter a role name');
      return;
    }

    try {
      // This would be an API call to add a role
      const updatedApplications = applications.map(app => 
        app.app_id === parseInt(appId)
          ? { ...app, roles: [...(app.roles || []), roleName] }
          : app
      );
      setApplications(updatedApplications);
      setNewRole({ appId: '', roleName: '' });
      setSuccess('Role added successfully');
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error adding role:', err);
      setError(`Failed to add role: ${err.message}`);
    }
  };

  const handleRemoveRole = async (appId, roleToRemove) => {
    try {
      // This would be an API call to remove a role
      const updatedApplications = applications.map(app =>
        app.app_id === appId
          ? { ...app, roles: (app.roles || []).filter(role => role !== roleToRemove) }
          : app
      );
      setApplications(updatedApplications);
      setSuccess('Role removed successfully');
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error removing role:', err);
      setError(`Failed to remove role: ${err.message}`);
    }
  };

  const handleAccessApp = (url) => {
    const formattedUrl = url.startsWith('http') ? url : `https://${url}`;
    window.open(formattedUrl, '_blank', 'noopener,noreferrer');
  };

  const handleAssignUser = async (appId, userId) => {
    try {
      await api.assignAppAdmin(userId, appId);
      await fetchAppAssignments(appId);
      setSuccess('User assigned successfully');
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error assigning user:', err);
      setError(`Failed to assign user: ${err.message}`);
    }
  };

  const handleRemoveUser = async (appId, userId) => {
    try {
      await api.removeAppAdmin(userId, appId);
      await fetchAppAssignments(appId);
      setSuccess('User removed successfully');
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error removing user:', err);
      setError(`Failed to remove user: ${err.message}`);
    }
  };

  const handleAppSelect = async (appId) => {
    setSelectedApp(appId);
    await fetchAppAssignments(appId);
  };

  if (loading) return <div className="loading">Loading applications...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="application-list-container">
      <h3>Applications</h3>

      {success && <div className="success-message">{success}</div>}

      {userType === 'admin' && (
        <div className="admin-controls">
          <div className="add-application-form">
            <h4>Add New Application</h4>
            <input
              type="text"
              value={newApp.name}
              onChange={(e) => setNewApp({ ...newApp, name: e.target.value })}
              placeholder="Application Name"
            />
            <input
              type="text"
              value={newApp.url}
              onChange={(e) => setNewApp({ ...newApp, url: e.target.value })}
              placeholder="Application URL (e.g., app.example.com)"
            />
            <input
              type="text"
              value={newApp.roles}
              onChange={(e) => setNewApp({ ...newApp, roles: e.target.value })}
              placeholder="Roles (comma-separated)"
            />
            <button onClick={handleAddApplication}>Add Application</button>
          </div>
        </div>
      )}

      {userType === 'app_admin' && (
        <div className="app-admin-controls">
          <div className="add-role-form">
            <h4>Add New Role</h4>
            <select
              value={newRole.appId}
              onChange={(e) => setNewRole({ ...newRole, appId: e.target.value })}
            >
              <option value="">Select Application</option>
              {applications.map(app => (
                <option key={app.app_id} value={app.app_id}>{app.app_name}</option>
              ))}
            </select>
            <input
              type="text"
              value={newRole.roleName}
              onChange={(e) => setNewRole({ ...newRole, roleName: e.target.value })}
              placeholder="Role Name"
            />
            <button onClick={() => handleAddRole(newRole.appId, newRole.roleName)}>Add Role</button>
          </div>
        </div>
      )}

      <ul className="applications-list">
        {applications.map((app) => (
          <li key={app.app_id} className="application-item">
            <div className="app-info">
              <span className="app-name">{app.app_name}</span>
              {userType === 'app_admin' && (
                <div className="app-roles">
                  <strong>Roles:</strong>
                  {(app.roles || []).map((role, index) => (
                    <span key={index} className="role-tag">
                      {role}
                      <button 
                        className="remove-role"
                        onClick={() => handleRemoveRole(app.app_id, role)}
                      >
                        Ã—
                      </button>
                    </span>
                  ))}
                </div>
              )}
            </div>
            <div className="app-actions">
              <button 
                className="access-button"
                onClick={() => handleAccessApp(app.app_url)}
              >
                Access
              </button>
              {userType === 'admin' && (
                <button 
                  className="manage-button" 
                  onClick={() => handleAppSelect(app.app_id)}
                >
                  Manage Users
                </button>
              )}
            </div>
          </li>
        ))}
      </ul>

      {userType === 'admin' && selectedApp && (
        <div className="user-assignment-section">
          <h4>Manage Users for {applications.find(app => app.app_id === selectedApp)?.app_name}</h4>
          <div className="user-list">
            <h5>Available Users</h5>
            <div className="user-grid">
              {users.map(user => (
                <div key={user.user_id} className="user-item">
                  <span>{user.username}</span>
                  {appAssignments[selectedApp]?.some(assignment => assignment.user_id === user.user_id) ? (
                    <button 
                      className="remove-user-button"
                      onClick={() => handleRemoveUser(selectedApp, user.user_id)}
                    >
                      Remove
                    </button>
                  ) : (
                    <button 
                      className="assign-user-button"
                      onClick={() => handleAssignUser(selectedApp, user.user_id)}
                    >
                      Assign
                    </button>
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default ApplicationList;
