import React, { useState } from 'react';
import './UserManagement.css';

function UserManagement() {
  const [users, setUsers] = useState([
    { id: 1, name: 'John Doe', assignedApps: [] },
    { id: 2, name: 'Jane Smith', assignedApps: [] },
  ]);
  const [newUser, setNewUser] = useState('');

  const handleAddUser = () => {
    if (newUser.trim()) {
      setUsers([...users, { id: users.length + 1, name: newUser, assignedApps: [] }]);
      setNewUser('');
    }
  };

  const handleAssignApp = (userId, appName) => {
    setUsers(users.map(user => 
      user.id === userId 
        ? { ...user, assignedApps: [...user.assignedApps, appName] } 
        : user
    ));
  };

  return (
    <div className="user-management-container">
      <h3>User Management</h3>
      <div className="add-user-form">
        <input
          type="text"
          value={newUser}
          onChange={(e) => setNewUser(e.target.value)}
          placeholder="Enter new user name"
        />
        <button onClick={handleAddUser}>Add User</button>
      </div>
      <ul>
        {users.map((user) => (
          <li key={user.id}>
            <span>{user.name}</span>
            <button onClick={() => handleAssignApp(user.id, 'App1')}>Assign to App1</button>
            <button onClick={() => handleAssignApp(user.id, 'App2')}>Assign to App2</button>
            <div>Assigned Apps: {user.assignedApps.join(', ')}</div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default UserManagement;
