import React, { useEffect, useState } from 'react';
import { api } from '../services/api';
import '../styles/AppAdminManagement.css';

function AppAdminManagement({ onLogout }) {
  const [appAdmins, setAppAdmins] = useState([]);
  const [applications, setApplications] = useState([]);
  const [newAdmin, setNewAdmin] = useState({
    username: '',
    email: '',
    password: '',
    selectedApps: []
  });
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [adminsData, appsData] = await Promise.all([
        api.getAppAdmins(),
        api.getApplications()
      ]);
      setAppAdmins(adminsData);
      setApplications(appsData);
    } catch (err) {
      setError('Failed to fetch data');
    }
  };

  const handleAddAdmin = async (e) => {
    e.preventDefault();
    if (!newAdmin.username || !newAdmin.email || !newAdmin.password || newAdmin.selectedApps.length === 0) {
      setError('Please fill in all required fields and select at least one application');
      return;
    }

    try {
      const adminData = {
        username: newAdmin.username,
        email: newAdmin.email,
        password: newAdmin.password,
        user_type: 'app_admin',
        applications: newAdmin.selectedApps
      };

      const response = await api.createAppAdmin(adminData);

      // Add the new admin to the list
      setAppAdmins(prevAdmins => [...prevAdmins, response]);

      // Clear the form
      setNewAdmin({
        username: '',
        email: '',
        password: '',
        selectedApps: []
      });

      setSuccess('App admin added successfully');
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      setError('Failed to add app admin');
    }
  };

  const handleDeleteAppAdmin = async (adminId) => {
    try {
      await api.deleteUser(adminId);
      setAppAdmins(prevAdmins => prevAdmins.filter(admin => admin.user_id !== adminId));
      setSuccess('App admin deleted successfully');
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      setError('Failed to delete app admin');
    }
  };

  const handleAppSelection = (appId) => {
    setNewAdmin(prev => ({
      ...prev,
      selectedApps: prev.selectedApps.includes(appId)
        ? prev.selectedApps.filter(id => id !== appId)
        : [...prev.selectedApps, appId]
    }));
  };

  const filteredAppAdmins = appAdmins.filter(admin => {
    const searchLower = searchTerm.toLowerCase();
    return (
      admin.username.toLowerCase().includes(searchLower) ||
      admin.email.toLowerCase().includes(searchLower) ||
      (admin.applications && admin.applications.some(app => 
        app.app_name.toLowerCase().includes(searchLower)
      ))
    );
  });

  return (
    <div className="app-admin-management">
      <div className="app-admin-management-header">
        <h2>App Admin Management</h2>
      </div>

      {error && <div className="error-message">{error}</div>}
      {success && <div className="success-message">{success}</div>}

      <div className="add-admin-section">
        <h3>Add New App Admin</h3>
        <form onSubmit={handleAddAdmin}>
          <div className="form-group">
            <input
              type="text"
              value={newAdmin.username}
              onChange={(e) => setNewAdmin({ ...newAdmin, username: e.target.value })}
              placeholder="Username"
              className="input-field"
            />
          </div>
          <div className="form-group">
            <input
              type="email"
              value={newAdmin.email}
              onChange={(e) => setNewAdmin({ ...newAdmin, email: e.target.value })}
              placeholder="Email"
              className="input-field"
            />
          </div>
          <div className="form-group">
            <input
              type="password"
              value={newAdmin.password}
              onChange={(e) => setNewAdmin({ ...newAdmin, password: e.target.value })}
              placeholder="Password"
              className="input-field"
              minLength="8"
            />
          </div>
          <div className="form-group">
            <label>Select Applications:</label>
            <div className="applications-grid">
              {applications.map(app => (
                <div key={app.app_id} className="application-checkbox">
                  <input
                    type="checkbox"
                    id={`app-${app.app_id}`}
                    checked={newAdmin.selectedApps.includes(app.app_id)}
                    onChange={() => handleAppSelection(app.app_id)}
                  />
                  <label htmlFor={`app-${app.app_id}`}>{app.app_name}</label>
                </div>
              ))}
            </div>
          </div>
          <button type="submit" className="add-button">Add App Admin</button>
        </form>
      </div>

      <div className="app-admins-list-container">
        <h3>App Admins List</h3>
        <input
          type="text"
          placeholder="Search app admins by username, email, or application..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="input-field search-input"
        />
        <div className="app-admins-table-container">
          <table className="app-admins-table">
            <thead>
              <tr>
                <th>Username</th>
                <th>Email</th>
                <th>Assigned Applications</th>
                <th>Created At</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredAppAdmins.map((admin) => (
                <tr key={admin.user_id}>
                  <td>{admin.username}</td>
                  <td>{admin.email}</td>
                  <td>
                    <div className="app-admin-apps">
                      {admin.applications && admin.applications.map((app, index) => (
                        <span key={index} className="app-admin-app-tag">
                          {app.app_name}
                        </span>
                      ))}
                    </div>
                  </td>
                  <td>{new Date(admin.created_at).toLocaleString('en-US', { timeZone: 'UTC' })}</td>
                  <td>
                    <button
                      className="delete-button"
                      onClick={() => handleDeleteAppAdmin(admin.user_id)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default AppAdminManagement;
