import React, { useEffect, useState } from 'react';
import { api } from '../services/api';
import '../styles/UserManagement.css';

function UserManagement({ userType }) {
  const [users, setUsers] = useState([]);
  const [applications, setApplications] = useState([]);
  const [newUser, setNewUser] = useState({ username: '', email: '', userType: 'end' });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  useEffect(() => {
    fetchUsers();
    fetchApplications();
  }, []);

  const fetchUsers = async () => {
    try {
      const data = await api.getUsers();
      setUsers(data);
      setLoading(false);
    } catch (err) {
      setError(`Failed to fetch users: ${err.message}`);
      setLoading(false);
    }
  };

  const fetchApplications = async () => {
    try {
      const data = await api.getApplications();
      setApplications(data);
    } catch (err) {
      setError(`Failed to fetch applications: ${err.message}`);
    }
  };

  const handleAddUser = async () => {
    if (newUser.username && newUser.email) {
      try {
        const result = await api.createUser(newUser);
        setUsers([...users, result]);
        setNewUser({ username: '', email: '', userType: 'end' });
        setSuccess('User added successfully');
        setTimeout(() => setSuccess(null), 3000);
      } catch (err) {
        setError(`Failed to add user: ${err.message}`);
      }
    } else {
      setError('Please fill in all required fields');
    }
  };

  const handleAssignAppAdmin = async (userId, appId) => {
    try {
      await api.assignAppAdmin(userId, appId);
      const updatedUsers = users.map(user =>
        user.user_id === userId
          ? { ...user, assignedApps: [...(user.assignedApps || []), appId] }
          : user
      );
      setUsers(updatedUsers);
      setSuccess('App admin assigned successfully');
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      setError(`Failed to assign app admin: ${err.message}`);
    }
  };

  const handleRemoveAppAdmin = async (userId, appId) => {
    try {
      await api.removeAppAdmin(userId, appId);
      const updatedUsers = users.map(user =>
        user.user_id === userId
          ? { ...user, assignedApps: user.assignedApps.filter(id => id !== appId) }
          : user
      );
      setUsers(updatedUsers);
      setSuccess('App admin removed successfully');
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      setError(`Failed to remove app admin: ${err.message}`);
    }
  };

  if (loading) return <div className="loading">Loading users...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="user-management-container">
      <h3>User Management</h3>

      {success && <div className="success-message">{success}</div>}

      {userType === 'admin' && (
        <div className="add-user-form">
          <input
            type="text"
            value={newUser.username}
            onChange={(e) => setNewUser({ ...newUser, username: e.target.value })}
            placeholder="Username"
          />
          <input
            type="email"
            value={newUser.email}
            onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
            placeholder="Email"
          />
          <select
            value={newUser.userType}
            onChange={(e) => setNewUser({ ...newUser, userType: e.target.value })}
          >
            <option value="end">End User</option>
            <option value="app_admin">App Admin</option>
          </select>
          <button onClick={handleAddUser}>Add User</button>
        </div>
      )}

      <ul className="users-list">
        {users.length === 0 ? (
          <div className="no-users">
            <p>No users found.</p>
          </div>
        ) : (
          users.map((user) => (
            <li key={user.user_id} className="user-item">
              <div className="user-info">
                <span className="user-name">{user.username}</span>
                <span className="user-email">{user.email}</span>
                <span className="user-type">{user.user_type}</span>

                {user.user_type === 'app_admin' && (
                  <div className="app-admin-assignments">
                    <strong>Assigned Applications:</strong>
                    {user.assignedApps && user.assignedApps.length > 0 ? (
                      user.assignedApps.map((appId) => {
                        const app = applications.find(a => a.app_id === appId);
                        return (
                          <div key={appId} className="assignment-tag">
                            {app ? app.app_name : 'Unknown App'}
                            {userType === 'admin' && (
                              <button 
                                className="remove-assignment"
                                onClick={() => handleRemoveAppAdmin(user.user_id, appId)}
                              >
                                Ã—
                              </button>
                            )}
                          </div>
                        );
                      })
                    ) : (
                      <span className="no-assignments">No applications assigned</span>
                    )}
                  </div>
                )}
              </div>

              {userType === 'admin' && user.user_type === 'app_admin' && (
                <div className="assignment-controls">
                  <select
                    onChange={(e) => {
                      const appId = parseInt(e.target.value);
                      if (appId) {
                        handleAssignAppAdmin(user.user_id, appId);
                        e.target.value = '';
                      }
                    }}
                    defaultValue=""
                  >
                    <option value="" disabled>Assign to Application</option>
                    {applications
                      .filter(app => !user.assignedApps?.includes(app.app_id))
                      .map(app => (
                        <option key={app.app_id} value={app.app_id}>
                          {app.app_name}
                        </option>
                      ))}
                  </select>
                </div>
              )}
            </li>
          ))
        )}
      </ul>
    </div>
  );
}

export default UserManagement;
