import * as d3 from 'd3';
import React, { useEffect, useRef } from 'react';
import '../styles/UserAccessGraph.css';

function UserAccessGraph({ applications }) {
  const svgRef = useRef();

  useEffect(() => {
    if (!applications || applications.length === 0) return;

    // Clear previous graph
    d3.select(svgRef.current).selectAll("*").remove();

    const width = 800;
    const height = 400;
    const margin = { top: 20, right: 20, bottom: 30, left: 40 };

    const svg = d3.select(svgRef.current)
      .attr('width', width)
      .attr('height', height);

    // Create scales
    const x = d3.scaleBand()
      .domain(applications.map(d => d.app_name))
      .range([margin.left, width - margin.right])
      .padding(0.1);

    const y = d3.scaleLinear()
      .domain([0, d3.max(applications, d => d.assigned_users?.length || 0)])
      .range([height - margin.bottom, margin.top]);

    // Add axes
    svg.append('g')
      .attr('transform', `translate(0,${height - margin.bottom})`)
      .call(d3.axisBottom(x))
      .selectAll('text')
      .attr('transform', 'rotate(-45)')
      .style('text-anchor', 'end');

    svg.append('g')
      .attr('transform', `translate(${margin.left},0)`)
      .call(d3.axisLeft(y));

    // Add bars
    svg.selectAll('rect')
      .data(applications)
      .join('rect')
      .attr('x', d => x(d.app_name))
      .attr('y', d => y(d.assigned_users?.length || 0))
      .attr('width', x.bandwidth())
      .attr('height', d => height - margin.bottom - y(d.assigned_users?.length || 0))
      .attr('fill', '#4a90e2');

    // Add title
    svg.append('text')
      .attr('x', width / 2)
      .attr('y', margin.top)
      .attr('text-anchor', 'middle')
      .style('font-size', '16px')
      .text('User Access Distribution');

  }, [applications]);

  return (
    <div className="user-access-graph">
      <svg ref={svgRef}></svg>
    </div>
  );
}

export default UserAccessGraph;
