import React, { useEffect, useState } from 'react';
import { api } from '../services/api';
import '../styles/AuditLog.css';

function AuditLog() {
  const [auditLogs, setAuditLogs] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortByRecent, setSortByRecent] = useState(true);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchAuditLogs();
  }, []);

  const fetchAuditLogs = async () => {
    try {
      const data = await api.getAuditLogs();
      setAuditLogs(data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching audit logs:', error);
      setLoading(false);
    }
  };

  const filteredAndSortedLogs = auditLogs
    .filter(log => 
      log.user.toLowerCase().includes(searchTerm.toLowerCase()) ||
      log.app.toLowerCase().includes(searchTerm.toLowerCase()) ||
      log.details.toLowerCase().includes(searchTerm.toLowerCase())
    )
    .sort((a, b) => {
      if (sortByRecent) {
        return new Date(b.timestamp) - new Date(a.timestamp);
      }
      return new Date(a.timestamp) - new Date(b.timestamp);
    });

  return (
    <div className="audit-log-container">
      <div className="audit-log-header">
        <h3>Audit Logs</h3>
        <div className="audit-log-controls">
          <input
            type="text"
            placeholder="Search by user, app, or details..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
          <button
            className={`sort-button ${sortByRecent ? 'active' : ''}`}
            onClick={() => setSortByRecent(!sortByRecent)}
          >
            {sortByRecent ? 'Most Recent First' : 'Oldest First'}
          </button>
        </div>
      </div>
      <div className="audit-log-table-container">
        {loading ? (
          <div className="loading">Loading audit logs...</div>
        ) : (
          <table>
            <thead>
              <tr>
                <th>User</th>
                <th>Application</th>
                <th>Action</th>
                <th>Details</th>
                <th>Timestamp</th>
              </tr>
            </thead>
            <tbody>
              {filteredAndSortedLogs.map((log, index) => (
                <tr key={index}>
                  <td>{log.user}</td>
                  <td>{log.app}</td>
                  <td>
                    <span className={`action-badge ${log.action.toLowerCase()}`}>
                      {log.action}
                    </span>
                  </td>
                  <td>{log.details}</td>
                  <td>{new Date(log.timestamp).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default AuditLog;
