const GITHUB_LOGIN_URL = 'https://github.com/login';

// Function to handle GitHub login with autofill
export const handleGitHubLogin = () => {
  // Create a form element
  const form = document.createElement('form');
  form.method = 'POST';
  form.action = GITHUB_LOGIN_URL;
  form.style.display = 'none';

  // Add username field
  const usernameInput = document.createElement('input');
  usernameInput.type = 'text';
  usernameInput.name = 'login';
  usernameInput.value = 'avneeshsingh0704@gmail.com';
  form.appendChild(usernameInput);

  // Add password field
  const passwordInput = document.createElement('input');
  passwordInput.type = 'password';
  passwordInput.name = 'password';
  passwordInput.value = 'Chupke1@';
  form.appendChild(passwordInput);

  // Add the form to the document
  document.body.appendChild(form);

  // Submit the form
  form.submit();

  // Remove the form after submission
  document.body.removeChild(form);
};

// Function to check if we're on the GitHub login page
export const isGitHubLoginPage = () => {
  return window.location.href.includes('github.com/login');
};

// Function to autofill GitHub credentials when on the login page
export const autofillGitHubCredentials = () => {
  if (isGitHubLoginPage()) {
    // Find the login input field
    const loginInput = document.querySelector('input[name="login"]');
    if (loginInput) {
      loginInput.value = 'avneeshsingh0704@gmail.com';
    }

    // Find the password input field
    const passwordInput = document.querySelector('input[name="password"]');
    if (passwordInput) {
      passwordInput.value = 'Chupke1@';
    }
  }
};

// Initialize autofill functionality
export const initGitHubAutofill = () => {
  // Check if we're on the GitHub login page
  if (isGitHubLoginPage()) {
    // Add a small delay to ensure the page is fully loaded
    setTimeout(autofillGitHubCredentials, 1000);
  }
};
