import React, { useEffect, useState } from 'react';
import { api } from '../services/api';
import '../styles/ApplicationList.css';

function ApplicationList({ userType }) {
  const [applications, setApplications] = useState([]);
  const [newApp, setNewApp] = useState({ name: '', url: '', roles: '' });
  const [newAppAdmin, setNewAppAdmin] = useState({ appId: '', userId: '' });
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [appsData, usersData] = await Promise.all([
        api.getApplications(),
        api.getUsers()
      ]);
      setApplications(appsData);
      setUsers(usersData);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching data:', err);
      setError(`Failed to fetch data: ${err.message}`);
      setLoading(false);
    }
  };

  const handleAddApplication = async () => {
    if (newApp.name && newApp.url) {
      try {
        const application = {
          app_name: newApp.name,
          app_url: newApp.url,
          description: newApp.roles,
          created_by: 2 // This should come from the logged-in user
        };

        const result = await api.createApplication(application);
        setApplications([...applications, result]);
        setNewApp({ name: '', url: '', roles: '' });
        setSuccess('Application added successfully');
        setTimeout(() => setSuccess(null), 3000);
      } catch (err) {
        console.error('Error adding application:', err);
        setError(`Failed to add application: ${err.message}`);
      }
    } else {
      setError('Please fill in all required fields');
    }
  };

  const handleAddAppAdmin = async () => {
    if (!newAppAdmin.appId || !newAppAdmin.userId) {
      setError('Please select both application and user');
      return;
    }

    try {
      await api.assignAppAdmin(newAppAdmin.appId, newAppAdmin.userId);
      const updatedApps = await api.getApplications();
      setApplications(updatedApps);
      setNewAppAdmin({ appId: '', userId: '' });
      setSuccess('App admin assigned successfully');
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error assigning app admin:', err);
      setError(`Failed to assign app admin: ${err.message}`);
    }
  };

  const handleRemoveAppAdmin = async (appId, userId) => {
    try {
      await api.removeAppAdmin(appId, userId);
      const updatedApps = await api.getApplications();
      setApplications(updatedApps);
      setSuccess('App admin removed successfully');
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Error removing app admin:', err);
      setError(`Failed to remove app admin: ${err.message}`);
    }
  };

  const handleAccessApp = (url) => {
    const formattedUrl = url.startsWith('http') ? url : `https://${url}`;
    window.open(formattedUrl, '_blank', 'noopener,noreferrer');
  };

  if (loading) return <div className="loading">Loading applications...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="application-list-container">
      <h3>Applications</h3>

      {success && <div className="success-message">{success}</div>}

      {userType === 'admin' && (
        <div className="admin-controls">
          <div className="add-application-form">
            <h4>Add New Application</h4>
            <input
              type="text"
              value={newApp.name}
              onChange={(e) => setNewApp({ ...newApp, name: e.target.value })}
              placeholder="Application Name"
            />
            <input
              type="text"
              value={newApp.url}
              onChange={(e) => setNewApp({ ...newApp, url: e.target.value })}
              placeholder="Application URL (e.g., app.example.com)"
            />
            <input
              type="text"
              value={newApp.roles}
              onChange={(e) => setNewApp({ ...newApp, roles: e.target.value })}
              placeholder="Roles (comma-separated)"
            />
            <button onClick={handleAddApplication}>Add Application</button>
          </div>

          <div className="add-app-admin-form">
            <h4>Add App Admin</h4>
            <select
              value={newAppAdmin.appId}
              onChange={(e) => setNewAppAdmin({ ...newAppAdmin, appId: e.target.value })}
            >
              <option value="">Select Application</option>
              {applications.map(app => (
                <option key={app.app_id} value={app.app_id}>{app.app_name}</option>
              ))}
            </select>
            <select
              value={newAppAdmin.userId}
              onChange={(e) => setNewAppAdmin({ ...newAppAdmin, userId: e.target.value })}
            >
              <option value="">Select User</option>
              {users.map(user => (
                <option key={user.user_id} value={user.user_id}>{user.username}</option>
              ))}
            </select>
            <button onClick={handleAddAppAdmin}>Assign App Admin</button>
          </div>
        </div>
      )}

      <ul className="applications-list">
        {applications.map((app) => (
          <li key={app.app_id} className="application-item">
            <div className="app-info">
              <span className="app-name">{app.app_name}</span>
              {userType === 'admin' && (
                <div className="app-admins">
                  <strong>App Admins:</strong>
                  {(app.app_admins || []).map((admin, index) => (
                    <span key={index} className="admin-tag">
                      {admin.username}
                      <button 
                        className="remove-admin"
                        onClick={() => handleRemoveAppAdmin(app.app_id, admin.user_id)}
                      >
                        Ã—
                      </button>
                    </span>
                  ))}
                </div>
              )}
            </div>
            <div className="app-actions">
              <button 
                className="access-button"
                onClick={() => handleAccessApp(app.app_url)}
              >
                Access
              </button>
              {(userType === 'admin' || userType === 'app_admin') && (
                <button className="manage-button" onClick={() => alert(`Manage ${app.app_name}`)}>Manage</button>
              )}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ApplicationList;
