import {
    CategoryScale,
    Chart as ChartJS,
    Legend,
    LinearScale,
    LineElement,
    PointElement,
    Title,
    Tooltip,
} from 'chart.js';
import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import '../styles/UserAccessGraph.css';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

function UserAccessGraph() {
  const [accessData, setAccessData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Using the exact data provided
    const mockData = [
      { user: 'John Doe', lastAccess: '2023-10-01T10:00:00' },
      { user: 'Jane Smith', lastAccess: '2023-10-01T11:30:00' },
      { user: 'Bob Johnson', lastAccess: '2023-10-02T09:15:00' },
    ];
    setAccessData(mockData);
    setLoading(false);
  }, []);

  const chartData = {
    labels: accessData.map(item => item.user),
    datasets: [
      {
        label: 'Last Access Time',
        data: accessData.map(item => new Date(item.lastAccess).getTime()),
        borderColor: 'var(--button-primary)',
        backgroundColor: 'var(--button-primary-transparent)',
        tension: 0.4,
        pointRadius: 6,
        pointHoverRadius: 8,
        pointBackgroundColor: 'var(--button-primary)',
        pointBorderColor: 'var(--card-bg)',
        pointBorderWidth: 2,
      },
    ],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
        labels: {
          color: 'var(--text-color)',
          font: {
            size: 14,
          },
          usePointStyle: true,
          pointStyle: 'circle',
        },
      },
      title: {
        display: true,
        text: 'User Last Access Times',
        color: 'var(--text-color)',
        font: {
          size: 16,
          weight: 'bold',
        },
      },
    },
    scales: {
      x: {
        grid: {
          color: 'var(--border-color)',
        },
        ticks: {
          color: 'var(--text-color)',
        },
      },
      y: {
        grid: {
          color: 'var(--border-color)',
        },
        ticks: {
          color: 'var(--text-color)',
          callback: function(value) {
            return new Date(value).toLocaleTimeString();
          }
        },
      },
    },
  };

  if (loading) {
    return <div className="loading">Loading graph data...</div>;
  }

  return (
    <div className="user-access-graph-container">
      <Line data={chartData} options={options} />
    </div>
  );
}

export default UserAccessGraph;
