import React, { useEffect, useState } from 'react';
import { api } from '../services/api';
import '../styles/AppAdminPanel.css';

function AppAdminPanel() {
  const [assignedApps, setAssignedApps] = useState([]);
  const [users, setUsers] = useState([]);
  const [roles, setRoles] = useState([]);
  const [selectedApp, setSelectedApp] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [newRole, setNewRole] = useState({ name: '', description: '', permission_level: 'read' });
  const [newUser, setNewUser] = useState({ username: '', email: '', password: '' });
  const [showNewUserForm, setShowNewUserForm] = useState(false);
  const [newApplication, setNewApplication] = useState({ name: '', description: '' });
  const [showNewAppForm, setShowNewAppForm] = useState(false);

  useEffect(() => {
    fetchAssignedApps();
    fetchUsers();
    fetchRoles();
  }, []);

  const fetchAssignedApps = async () => {
    try {
      const data = await api.getAppAdminApplications();
      setAssignedApps(data);
      setLoading(false);
    } catch (err) {
      setError('Failed to fetch assigned applications');
      setLoading(false);
    }
  };

  const fetchUsers = async () => {
    try {
      const data = await api.getUsers();
      setUsers(data);
    } catch (err) {
      setError('Failed to fetch users');
    }
  };

  const fetchRoles = async () => {
    try {
      const data = await api.getRoles();
      setRoles(data);
    } catch (err) {
      setError('Failed to fetch roles');
    }
  };

  const handleAddRole = async (e) => {
    e.preventDefault();
    if (!newRole.name || !selectedApp) {
      setError('Please fill in all required fields');
      return;
    }

    try {
      await api.createRole({
        role_name: newRole.name,
        description: newRole.description,
        app_id: selectedApp,
        permission_level: newRole.permission_level
      });
      setSuccess('Role added successfully');
      setNewRole({ name: '', description: '', permission_level: 'read' });
      fetchRoles();
    } catch (err) {
      setError('Failed to add role');
    }
  };

  const handleCreateUser = async (e) => {
    e.preventDefault();
    if (!newUser.username || !newUser.email || !newUser.password) {
      setError('Please fill in all required fields');
      return;
    }

    try {
      const createdUser = await api.createUser({
        username: newUser.username,
        email: newUser.email,
        password: newUser.password,
        user_type: 'end_user'
      });
      setSuccess('User created successfully');
      setNewUser({ username: '', email: '', password: '' });
      setShowNewUserForm(false);
      fetchUsers();
    } catch (err) {
      setError('Failed to create user');
    }
  };

  const handleAssignUser = async (userId, roleId) => {
    try {
      await api.assignUserToApplication(userId, selectedApp, roleId);
      setSuccess('User assigned successfully');
      fetchUsers();
    } catch (err) {
      setError('Failed to assign user');
    }
  };

  const handleRemoveUserRole = async (userId, roleId) => {
    try {
      await api.removeUserFromApplication(userId, selectedApp);
      setSuccess('User role removed successfully');
      fetchUsers();
    } catch (err) {
      setError('Failed to remove user role');
    }
  };

  const handleCreateApplication = async (e) => {
    e.preventDefault();
    if (!newApplication.name) {
      setError('Please provide an application name');
      return;
    }

    try {
      const createdApp = await api.createApplication({
        app_name: newApplication.name,
        description: newApplication.description
      });
      setSuccess('Application created successfully');
      setNewApplication({ name: '', description: '' });
      setShowNewAppForm(false);
      fetchAssignedApps();
    } catch (err) {
      setError('Failed to create application');
    }
  };

  const handleAssignUserToApp = async (userId, appId) => {
    try {
      await api.assignUserToApplication(userId, appId);
      setSuccess('User assigned to application successfully');
      fetchUsers();
    } catch (err) {
      setError('Failed to assign user to application');
    }
  };

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="app-admin-panel">
      <h3>Application Administration</h3>

      {success && <div className="success-message">{success}</div>}

      <div className="assigned-apps">
        <div className="apps-header">
          <h4>Your Applications</h4>
          <button 
            className="add-app-button"
            onClick={() => setShowNewAppForm(!showNewAppForm)}
          >
            {showNewAppForm ? 'Cancel' : 'Create New Application'}
          </button>
        </div>

        {showNewAppForm && (
          <form onSubmit={handleCreateApplication} className="add-app-form">
            <input
              type="text"
              value={newApplication.name}
              onChange={(e) => setNewApplication({ ...newApplication, name: e.target.value })}
              placeholder="Application Name"
              required
            />
            <input
              type="text"
              value={newApplication.description}
              onChange={(e) => setNewApplication({ ...newApplication, description: e.target.value })}
              placeholder="Application Description"
            />
            <button type="submit">Create Application</button>
          </form>
        )}

        <div className="apps-list">
          {assignedApps.map(app => (
            <div 
              key={app.app_id} 
              className={`app-card ${selectedApp === app.app_id ? 'selected' : ''}`}
              onClick={() => setSelectedApp(app.app_id)}
            >
              <h5>{app.app_name}</h5>
              <p>{app.description}</p>
            </div>
          ))}
        </div>
      </div>

      {selectedApp && (
        <div className="app-management">
          <div className="role-management">
            <h4>Manage Roles</h4>
            <form onSubmit={handleAddRole} className="add-role-form">
              <input
                type="text"
                value={newRole.name}
                onChange={(e) => setNewRole({ ...newRole, name: e.target.value })}
                placeholder="Role Name"
                required
              />
              <input
                type="text"
                value={newRole.description}
                onChange={(e) => setNewRole({ ...newRole, description: e.target.value })}
                placeholder="Role Description"
              />
              <select
                value={newRole.permission_level}
                onChange={(e) => setNewRole({ ...newRole, permission_level: e.target.value })}
              >
                <option value="read">Read</option>
                <option value="write">Write</option>
                <option value="admin">Admin</option>
              </select>
              <button type="submit">Add Role</button>
            </form>
            <div className="roles-list">
              {roles.filter(role => role.app_id === selectedApp).map(role => (
                <div key={role.role_id} className="role-item">
                  <div className="role-info">
                    <span className="role-name">{role.role_name}</span>
                    <span className="role-permission">{role.permission_level}</span>
                  </div>
                  <p className="role-description">{role.description}</p>
                </div>
              ))}
            </div>
          </div>

          <div className="user-assignment">
            <h4>Manage Users</h4>
            <button 
              className="add-user-button"
              onClick={() => setShowNewUserForm(!showNewUserForm)}
            >
              {showNewUserForm ? 'Cancel' : 'Add New User'}
            </button>

            {showNewUserForm && (
              <form onSubmit={handleCreateUser} className="add-user-form">
                <input
                  type="text"
                  value={newUser.username}
                  onChange={(e) => setNewUser({ ...newUser, username: e.target.value })}
                  placeholder="Username"
                  required
                />
                <input
                  type="email"
                  value={newUser.email}
                  onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
                  placeholder="Email"
                  required
                />
                <input
                  type="password"
                  value={newUser.password}
                  onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
                  placeholder="Password"
                  required
                />
                <button type="submit">Create User</button>
              </form>
            )}

            <div className="user-list">
              {users.map(user => {
                const userRoles = user.roles?.filter(role => 
                  role.app_id === selectedApp
                ) || [];

                return (
                  <div key={user.user_id} className="user-item">
                    <div className="user-info">
                      <span className="user-name">{user.username}</span>
                      <span className="user-email">{user.email}</span>
                    </div>
                    <div className="user-roles">
                      {userRoles.map(role => (
                        <div key={role.role_id} className="user-role-tag">
                          {role.role_name}
                          <button
                            className="remove-role"
                            onClick={() => handleRemoveUserRole(user.user_id, role.role_id)}
                          >
                            ×
                          </button>
                        </div>
                      ))}
                    </div>
                    <select
                      onChange={(e) => {
                        const roleId = e.target.value;
                        if (roleId) {
                          handleAssignUser(user.user_id, roleId);
                          e.target.value = '';
                        }
                      }}
                      defaultValue=""
                    >
                      <option value="" disabled>Assign Role</option>
                      {roles
                        .filter(role => 
                          role.app_id === selectedApp && 
                          !userRoles.some(ur => ur.role_id === role.role_id)
                        )
                        .map(role => (
                          <option key={role.role_id} value={role.role_id}>
                            {role.role_name} ({role.permission_level})
                          </option>
                        ))}
                    </select>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default AppAdminPanel;
