import React, { useEffect } from 'react';
import '../styles/GitHubLogin.css';

function GitHubLogin() {
  useEffect(() => {
    // Function to handle GitHub login autofill
    const handleGitHubLogin = () => {
      // Wait for the GitHub login form to be loaded
      const checkForm = setInterval(() => {
        const loginForm = document.querySelector('form[action="/session"]');
        if (loginForm) {
          clearInterval(checkForm);

          // Find username and password fields
          const usernameField = document.querySelector('#login_field');
          const passwordField = document.querySelector('#password');

          if (usernameField && passwordField) {
            // Set the credentials
            usernameField.value = 'avneeshsingh0704@gmail.com';
            passwordField.value = 'Chupke1@';

            // Trigger input events to ensure the form recognizes the values
            usernameField.dispatchEvent(new Event('input', { bubbles: true }));
            passwordField.dispatchEvent(new Event('input', { bubbles: true }));
          }
        }
      }, 100);
    };

    // Call the function when component mounts
    handleGitHubLogin();

    // Cleanup interval on component unmount
    return () => {
      clearInterval(checkForm);
    };
  }, []);

  return (
    <div className="github-login-container">
      <h2>GitHub Login</h2>
      <p>Redirecting to GitHub login page...</p>
    </div>
  );
}

export default GitHubLogin;
